import React, { useEffect, useState } from 'react'
import "./Home.css"
import axios from 'axios'
import { Link } from 'react-router-dom';

function Home() {
    const [data, setdata] = useState([]);

    useEffect(()=>{
        getStudent();
    },[])

    const getStudent = async ()=>{
        const res = await axios.get('http://localhost:5000/users');
        if(res.status===200){
            setdata(res.data)
        }
    }

    const ondeleteUser = async (id)=>{
        if(window.confirm("Are you Sure")){
            const res = await axios.delete(`http://localhost:5000/user/${id}`);
            if(res.status===200){
                alert(res.data);
                getStudent();
            }
        }
       
    }
  return (
    <div style={{marginTop: "150px"}}>
        <table className='styled-table'>
            <thead>
                <tr>
                    <th style={{textAlign:"center"}}>No</th>
                    <th style={{textAlign:"center"}}>name</th>
                    <th style={{textAlign:"center"}}>email</th>
                    <th style={{textAlign:"center"}}>mobileNo</th>
                    <th style={{textAlign:"center"}}>gender</th>
                    <th style={{textAlign:"center"}}>hobbies</th>
                </tr>
            </thead>
            <tbody>
                {data && data.map((item,index)=>{
                    return(
                        <tr key={index}>
                            <th scope='row'>{index+1}</th>
                            <td>{item.name}</td>
                            <td>{item.email}</td>

                            <td>{item.mobileNo}</td>

                            <td>{item.gender}</td>
                            <td>{item.hobbies}</td>

                            <td>
                                <Link to={`/update/${item.id}`}>
                                    <button className='btn btn-edit'>edit</button>
                                </Link>
                                <button className='btn btn-delete' onClick={ondeleteUser(item.id)}>delete</button>

                            </td>

                        </tr>
                    )
                })}
            </tbody>
        </table>
    </div>
  )
}

export default Home;
